import React, { useState } from "react";

function App() {
  const [text, setText] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [grade, setGrade] = useState("ielts_task2");
  const [isNeedSynonyms, setIsNeedSynonyms] = useState("false");
  const [correctVersion, setCorrectVersion] = useState("advanced");
  const [isNeedEssayReport, setIsNeedEssayReport] = useState("true");
  const [title, setTitle] = useState("");
  const [modelContent, setModelContent] = useState("");

  const handleSubmit = async () => {
    setLoading(true);
    setResult(null);
    try {
      const resp = await fetch("http://localhost:8000/score", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          text,
          grade,
          isNeedSynonyms,
          correctVersion,
          isNeedEssayReport,
          title,
          modelContent
        }),
      });
      const data = await resp.json();
      setResult(data);
    } catch (e) {
      setResult({ error: "请求失败" });
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-4">
      <h1 className="text-2xl font-bold mb-4">雅思作文自动评分</h1>
      <div className="w-full max-w-xl mb-2 flex flex-wrap gap-2">
        <div>
          <label className="mr-2">作文等级:</label>
          <select value={grade} onChange={e => setGrade(e.target.value)} className="border rounded p-1">
            <option value="ielts_task2">雅思Task2</option>
            <option value="ielts_task1">雅思Task1</option>
            <option value="cet4">CET4</option>
            <option value="cet6">CET6</option>
            {/* 可根据API文档补充更多等级 */}
          </select>
        </div>
        <div>
          <label className="mr-2">是否查询同义词:</label>
          <select value={isNeedSynonyms} onChange={e => setIsNeedSynonyms(e.target.value)} className="border rounded p-1">
            <option value="false">否</option>
            <option value="true">是</option>
          </select>
        </div>
        <div>
          <label className="mr-2">批改版本:</label>
          <select value={correctVersion} onChange={e => setCorrectVersion(e.target.value)} className="border rounded p-1">
            <option value="basic">基础</option>
            <option value="advanced">高级</option>
          </select>
        </div>
        <div>
          <label className="mr-2">返回写作报告:</label>
          <select value={isNeedEssayReport} onChange={e => setIsNeedEssayReport(e.target.value)} className="border rounded p-1">
            <option value="true">是</option>
            <option value="false">否</option>
          </select>
        </div>
      </div>
      <div className="w-full max-w-xl mb-2">
        <input
          className="w-full p-2 border rounded mb-2"
          placeholder="作文标题（可选）"
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        <textarea
          className="w-full h-20 p-2 border rounded"
          placeholder="范文（可选）"
          value={modelContent}
          onChange={e => setModelContent(e.target.value)}
        />
      </div>
      <textarea
        className="w-full max-w-xl h-40 p-2 border rounded mb-4"
        placeholder="请输入雅思作文..."
        value={text}
        onChange={e => setText(e.target.value)}
      />
      <button
        className="bg-blue-500 text-white px-6 py-2 rounded mb-4 disabled:opacity-50"
        onClick={handleSubmit}
        disabled={loading || !text.trim()}
      >
        {loading ? "评分中..." : "提交评分"}
      </button>
      {result && (
        <div className="w-full max-w-xl bg-white p-4 rounded shadow">
          {result.error ? (
            <div className="text-red-500">{result.error}</div>
          ) : (
            <>
              <div className="text-lg font-semibold mb-2">总分: {result.score}</div>
              {result.details && (
                <div className="mb-2">
                  <div>语法分: {result.details.grammarScore}</div>
                  <div>词汇分: {result.details.wordScore}</div>
                  <div>结构分: {result.details.structureScore}</div>
                  <div>话题分: {result.details.topicScore}</div>
                  <div className="text-gray-600 text-sm mt-1">语法建议: {result.details.grammarAdvice}</div>
                  <div className="text-gray-600 text-sm">词汇建议: {result.details.wordAdvice}</div>
                  <div className="text-gray-600 text-sm">结构建议: {result.details.structureAdvice}</div>
                </div>
              )}
              <div className="text-gray-700">建议: {result.advice}</div>
              {result.essayFeedback && result.essayFeedback.sentsFeedback && (
                <div className="mt-4">
                  <div className="font-semibold mb-1">句子逐句反馈：</div>
                  {result.essayFeedback.sentsFeedback.map((item, idx) => (
                    <div key={idx} className="mb-1">
                      <div>
                        <span className="font-medium">原句：</span>{item.rawSent}
                      </div>
                      {item.sentFeedback && (
                        <div className="text-red-600 text-sm">建议：{item.sentFeedback}</div>
                      )}
                      <div className="text-gray-500 text-xs">得分：{item.sentScore}</div>
                    </div>
                  ))}
                </div>
              )}
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
