import React, { useState } from "react";

function App() {
  const [text, setText] = useState("");
  const [grade, setGrade] = useState("ielts_task2");
  const [title, setTitle] = useState("");
  const [modelContent, setModelContent] = useState("");
  const [isNeedSynonyms, setIsNeedSynonyms] = useState("false");
  const [correctVersion, setCorrectVersion] = useState("advanced");
  const [isNeedEssayReport, setIsNeedEssayReport] = useState("true");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    setLoading(true);
    setResult(null);
    try {
      const resp = await fetch("http://localhost:8000/score", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          text,
          grade,
          title,
          modelContent,
          isNeedSynonyms,
          correctVersion,
          isNeedEssayReport
        }),
      });
      const data = await resp.json();
      setResult(data);
    } catch (e) {
      setResult({ error: "请求失败" });
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-4">
      <h1 className="text-2xl font-bold mb-4">雅思作文自动评分</h1>
      <div className="w-full max-w-xl bg-white p-4 rounded shadow mb-4">
        <div className="mb-2">
          <label className="font-medium">作文等级：</label>
          <select value={grade} onChange={e => setGrade(e.target.value)} className="border rounded p-1 ml-2">
            <option value="ielts_task2">雅思Task2</option>
            <option value="cet4">CET4</option>
            <option value="cet6">CET6</option>
            <option value="toefl">TOEFL</option>
          </select>
        </div>
        <div className="mb-2">
          <label className="font-medium">是否查询同义词：</label>
          <select value={isNeedSynonyms} onChange={e => setIsNeedSynonyms(e.target.value)} className="border rounded p-1 ml-2">
            <option value="false">否</option>
            <option value="true">是</option>
          </select>
        </div>
        <div className="mb-2">
          <label className="font-medium">作文批改版本：</label>
          <select value={correctVersion} onChange={e => setCorrectVersion(e.target.value)} className="border rounded p-1 ml-2">
            <option value="basic">基础</option>
            <option value="advanced">高级</option>
          </select>
        </div>
        <div className="mb-2">
          <label className="font-medium">是否返回写作报告：</label>
          <select value={isNeedEssayReport} onChange={e => setIsNeedEssayReport(e.target.value)} className="border rounded p-1 ml-2">
            <option value="true">是</option>
            <option value="false">否</option>
          </select>
        </div>
        <div className="mb-2">
          <label className="font-medium">作文标题：</label>
          <input value={title} onChange={e => setTitle(e.target.value)} className="border rounded p-1 ml-2 w-3/4" />
        </div>
        <div className="mb-2">
          <label className="font-medium">范文：</label>
          <textarea value={modelContent} onChange={e => setModelContent(e.target.value)} className="border rounded p-1 ml-2 w-3/4 h-16" />
        </div>
      </div>
      <textarea
        className="w-full max-w-xl h-40 p-2 border rounded mb-4"
        placeholder="请输入雅思作文..."
        value={text}
        onChange={e => setText(e.target.value)}
      />
      <button
        className="bg-blue-500 text-white px-6 py-2 rounded mb-4 disabled:opacity-50"
        onClick={handleSubmit}
        disabled={loading || !text.trim()}
      >
        {loading ? "评分中..." : "提交评分"}
      </button>
      {result && (
        <div className="w-full max-w-xl bg-white p-4 rounded shadow">
          {result.error ? (
            <div className="text-red-500">{result.error}</div>
          ) : (
            <>
              <div className="text-lg font-semibold mb-2">总分: {result.score}</div>
              {/* 各项分数 */}
              {result.majorScore && (
                <div className="mb-2">
                  <div>语法分: {result.majorScore.grammarScore}</div>
                  <div>词汇分: {result.majorScore.wordScore}</div>
                  <div>结构分: {result.majorScore.structureScore}</div>
                  <div>话题分: {result.majorScore.topicScore}</div>
                  <div className="text-gray-600 text-sm mt-1">语法建议: {result.majorScore.grammarAdvice}</div>
                  <div className="text-gray-600 text-sm">词汇建议: {result.majorScore.wordAdvice}</div>
                  <div className="text-gray-600 text-sm">结构建议: {result.majorScore.structureAdvice}</div>
                </div>
              )}
              <div className="text-gray-700">综合建议: {result.advice}</div>
              {/* 句子逐句反馈 */}
              {result.essayFeedback && result.essayFeedback.sentsFeedback && (
                <div className="mt-4">
                  <div className="font-semibold mb-1">句子逐句反馈：</div>
                  {result.essayFeedback.sentsFeedback.map((item, idx) => (
                    <div key={idx} className="mb-1 border-b pb-2">
                      <div>
                        <span className="font-medium">原句：</span>{item.rawSent}
                      </div>
                      {item.sentFeedback && (
                        <div className="text-red-600 text-sm">建议：{item.sentFeedback}</div>
                      )}
                      {/* 展示同义词建议 */}
                      {item.errorPosInfos && item.errorPosInfos.length > 0 && (
                        <div className="text-blue-600 text-sm">
                          {item.errorPosInfos
                            .filter(e => e.type === "synonym" && e.target && e.target.length > 0)
                            .map((e, i) => (
                              <div key={i}>
                                推荐同义词：{e.target.join("、")}
                              </div>
                            ))}
                        </div>
                      )}
                      <div className="text-gray-500 text-xs">得分：{item.sentScore}</div>
                    </div>
                  ))}
                </div>
              )}
              {/* 写作报告 */}
              {result.essayReport && (
                <div className="mt-4">
                  <div className="font-semibold mb-1">写作报告：</div>
                  <div>平均句长：{result.essayReport.avgSentLen}</div>
                  <div>句长丰富度：{result.essayReport.stdSentLen}</div>
                  <div>句数：{result.essayReport.sentNum}</div>
                  <div>逻辑连接词数：{result.essayReport.conjWordNum}</div>
                  <div>词汇分布建议：<span dangerouslySetInnerHTML={{__html: result.essayReport.lexicalDistribution?.advice || ''}} /></div>
                  <div>句长建议：{result.essayReport.avgSentLenAdvice}</div>
                  <div>句长丰富度建议：{result.essayReport.stdSentLenAdvice}</div>
                  <div>结构分建议：{result.essayReport.sentenceStructureAdvice || result.essayReport.sentComplexInfo?.sentenceStructureAdvice}</div>
                  {/* 逻辑连接词类型分布 */}
                  {result.essayReport.conjTypeInfos && (
                    <div className="mt-2">
                      <div className="font-medium">逻辑连接词类型分布：</div>
                      {result.essayReport.conjTypeInfos.map((c, i) => (
                        <div key={i}>{c.name}: {c.count} ({(c.percent * 100).toFixed(1)}%)</div>
                      ))}
                    </div>
                  )}
                </div>
              )}
              {/* 其它特征分 */}
              {result.allFeatureScore && (
                <div className="mt-4">
                  <div className="font-semibold mb-1">其它特征分：</div>
                  {Object.entries(result.allFeatureScore).map(([k, v]) => (
                    <div key={k}>{k}: {v}</div>
                  ))}
                </div>
              )}
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
