import React, { useState } from "react";

function App() {
  const [text, setText] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    setLoading(true);
    setResult(null);
    try {
      const resp = await fetch("http://localhost:8000/score", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text }),
      });
      const data = await resp.json();
      setResult(data);
    } catch (e) {
      setResult({ error: "请求失败" });
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-4">
      <h1 className="text-2xl font-bold mb-4">雅思作文自动评分</h1>
      <textarea
        className="w-full max-w-xl h-40 p-2 border rounded mb-4"
        placeholder="请输入雅思作文..."
        value={text}
        onChange={e => setText(e.target.value)}
      />
      <button
        className="bg-blue-500 text-white px-6 py-2 rounded mb-4 disabled:opacity-50"
        onClick={handleSubmit}
        disabled={loading || !text.trim()}
      >
        {loading ? "评分中..." : "提交评分"}
      </button>
      {result && (
        <div className="w-full max-w-xl bg-white p-4 rounded shadow">
          {result.error ? (
            <div className="text-red-500">{result.error}</div>
          ) : (
            <>
              <div className="text-lg font-semibold mb-2">总分: {result.score}</div>
              {result.details && (
                <div className="mb-2">
                  {Object.entries(result.details).map(([k, v]) => (
                    <div key={k}>{k}: {v}</div>
                  ))}
                </div>
              )}
              <div className="text-gray-700">建议: {result.advice}</div>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
